import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { X, Plus, Play } from "lucide-react";

interface Team {
  name: string;
  score: number;
  color: string;
}

const TEAM_COLORS = [
  { name: "Purple", value: "purple", bg: "bg-[#9747FF]", border: "border-[#9747FF]" },
  { name: "Cyan", value: "cyan", bg: "bg-[#14D4E8]", border: "border-[#14D4E8]" },
  { name: "Orange", value: "orange", bg: "bg-[#FF8A3D]", border: "border-[#FF8A3D]" },
  { name: "Green", value: "green", bg: "bg-green-500", border: "border-green-500" },
  { name: "Pink", value: "pink", bg: "bg-pink-500", border: "border-pink-500" },
  { name: "Yellow", value: "yellow", bg: "bg-yellow-500", border: "border-yellow-500" },
];

interface TeamSetupProps {
  onStart: (teams: Team[], numberOfRounds: string) => void;
  onBack: () => void;
  initialTeams?: Team[];
}

export function TeamSetup({ onStart, onBack, initialTeams }: TeamSetupProps) {
  const [teams, setTeams] = useState<Team[]>(() => {
    if (initialTeams && initialTeams.length > 0) {
      return initialTeams.map(team => ({ ...team, score: 0 }));
    }
    return [
      { name: "", score: 0, color: "purple" },
      { name: "", score: 0, color: "cyan" },
    ];
  });
  const [numberOfRounds, setNumberOfRounds] = useState<string>("5");

  const addTeam = () => {
    if (teams.length < 6) {
      const usedColors = new Set(teams.map(t => t.color));
      const availableColor = TEAM_COLORS.find(c => !usedColors.has(c.value))?.value || "purple";
      setTeams([...teams, { name: "", score: 0, color: availableColor }]);
    }
  };

  const removeTeam = (index: number) => {
    if (teams.length > 2) {
      setTeams(teams.filter((_, i) => i !== index));
    }
  };

  const updateTeamName = (index: number, name: string) => {
    const newTeams = [...teams];
    newTeams[index].name = name;
    setTeams(newTeams);
  };

  const updateTeamColor = (index: number, color: string) => {
    const newTeams = [...teams];
    newTeams[index].color = color;
    setTeams(newTeams);
  };

  const canStart = teams.every(t => t.name.trim().length > 0);

  const handleStart = () => {
    if (canStart) {
      if (navigator.vibrate) {
        navigator.vibrate(50);
      }
      onStart(teams, numberOfRounds);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 via-purple-600 to-cyan-500 flex flex-col p-6">
      <button
        onClick={onBack}
        className="self-start text-white/90 hover:text-white transition-colors mb-6"
        data-testid="button-back-team-setup"
      >
        â† Back
      </button>

      <div className="flex-1 flex flex-col items-center justify-center max-w-md mx-auto w-full">
        <h1 className="text-4xl font-bold text-white mb-2 text-center">
          Team Setup
        </h1>
        <p className="text-white/90 text-center mb-8">
          Create {teams.length} teams to compete!
        </p>

        <div className="space-y-4 w-full mb-6">
          {teams.map((team, index) => (
            <div key={index} className="bg-white/10 backdrop-blur-sm rounded-xl p-4 border-2 border-white/20" data-testid={`team-card-${index}`}>
              <div className="flex items-center gap-3 mb-3">
                <div className="flex-1">
                  <Input
                    placeholder={`Team ${index + 1} Name`}
                    value={team.name}
                    onChange={(e) => updateTeamName(index, e.target.value)}
                    className="bg-white/20 border-white/30 text-white placeholder:text-white/60 text-lg h-12"
                    data-testid={`input-team-name-${index}`}
                    maxLength={20}
                  />
                </div>
                {teams.length > 2 && (
                  <Button
                    size="icon"
                    variant="ghost"
                    onClick={() => removeTeam(index)}
                    className="text-white/80 hover:text-white hover:bg-white/10"
                    data-testid={`button-remove-team-${index}`}
                  >
                    <X className="w-5 h-5" />
                  </Button>
                )}
              </div>

              <div className="flex gap-2 flex-wrap">
                {TEAM_COLORS.map((colorOption) => (
                  <button
                    key={colorOption.value}
                    onClick={() => updateTeamColor(index, colorOption.value)}
                    className={`w-10 h-10 rounded-lg ${colorOption.bg} ${
                      team.color === colorOption.value
                        ? `ring-4 ring-white scale-110`
                        : "opacity-60 hover:opacity-100"
                    } transition-all`}
                    data-testid={`button-color-${colorOption.value}-${index}`}
                    aria-label={`Select ${colorOption.name}`}
                  />
                ))}
              </div>
            </div>
          ))}
        </div>

        {teams.length < 6 && (
          <Button
            onClick={addTeam}
            variant="outline"
            className="w-full mb-6 bg-white/10 border-white/30 text-white hover:bg-white/20 h-12"
            data-testid="button-add-team"
          >
            <Plus className="w-5 h-5 mr-2" />
            Add Team
          </Button>
        )}

        <div className="w-full mb-6 bg-white/10 backdrop-blur-sm rounded-xl p-4 border-2 border-white/20">
          <h3 className="text-lg font-semibold text-white mb-3">Number of Rounds</h3>
          <div className="grid grid-cols-4 gap-2">
            {[
              { value: "3", label: "3" },
              { value: "5", label: "5" },
              { value: "10", label: "10" },
              { value: "infinite", label: "âˆž" },
            ].map((option) => (
              <Button
                key={option.value}
                variant={numberOfRounds === option.value ? "default" : "outline"}
                className={`h-12 font-semibold ${
                  numberOfRounds === option.value
                    ? "bg-white text-purple-600 hover:bg-white/90"
                    : "bg-white/10 border-white/30 text-white hover:bg-white/20"
                }`}
                onClick={() => setNumberOfRounds(option.value)}
                data-testid={`rounds-${option.value}`}
              >
                {option.label}
              </Button>
            ))}
          </div>
        </div>

        <Button
          onClick={handleStart}
          disabled={!canStart}
          className="w-full bg-white text-purple-600 hover:bg-white/90 disabled:opacity-50 disabled:cursor-not-allowed h-14 text-lg font-semibold"
          data-testid="button-start-team-game"
        >
          <Play className="w-6 h-6 mr-2" />
          Start Team Game
        </Button>

        {!canStart && (
          <p className="text-white/70 text-sm mt-3 text-center">
            Please name all teams to continue
          </p>
        )}
      </div>
    </div>
  );
}

client/src/components/game/CategorySelec.content:
import { Button } from "@/components/ui/button";
import { ArrowLeft } from "lucide-react";
import { allCategories } from "@/lib/categories";
import { CategoryCard } from "./CategoryCard";
import { isDeckUnlocked } from "@/lib/premium";

interface CategorySelectProps {
  selectedCategories: string[];
  onCategoryToggle: (categoryId: string) => void;
  onStartPlaying: () => void;
  onBack: () => void;
  onOpenSubscription?: () => void;
  isPremium?: boolean;
}

const FREE_CATEGORY_LIMIT = 5;

export function CategorySelect({
  selectedCategories,
  onCategoryToggle,
  onStartPlaying,
  onBack,
  onOpenSubscription,
  isPremium = false,
}: CategorySelectProps) {
  const canStart = selectedCategories.length > 0;

  const handleCategoryClick = (categoryId: string, isLocked: boolean | undefined) => {
    if (isLocked) {
      onOpenSubscription?.();
    } else {
      onCategoryToggle(categoryId);
    }
  };

  return (
    <div className="min-h-screen flex flex-col p-6 md:p-8">
      <div className="flex items-center justify-between mb-8">
        <Button
          size="icon"
          variant="ghost"
          onClick={onBack}
          data-testid="button-back"
        >
          <ArrowLeft className="h-6 w-6" />
        </Button>
        <h2 className="text-2xl md:text-3xl font-bold text-foreground">
          Choose Categories
        </h2>
        <div className="w-10" />
      </div>

      <div className="flex-1 max-w-4xl w-full mx-auto">
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 mb-8">
          {allCategories.map((category) => {
            const isLocked = !isDeckUnlocked(category.id) && category.premium;
            return (
              <CategoryCard
                key={category.id}
                category={category}
                isSelected={selectedCategories.includes(category.id)}
                onToggle={() => handleCategoryClick(category.id, isLocked)}
                isLocked={isLocked}
                onLockedClick={onOpenSubscription}
              />
            );
          })}
        </div>
      </div>

      <div className="fixed bottom-0 left-0 right-0 p-6 bg-gradient-to-t from-background via-background to-transparent">
        <Button
          size="lg"
          className="w-full max-w-md mx-auto block h-16 text-xl font-semibold rounded-full"
          onClick={onStartPlaying}
          disabled={!canStart}
          data-testid="button-start-playing"
        >
          {canStart
            ? `Start Playing (${selectedCategories.length} ${selectedCategories.length === 1 ? "Category" : "Categories"})`
            : "Select at least 1 category"}
        </Button>
      </div>
    </div>
  );
}

client/src/hooks/useGameState.ts.content:
import { useState, useEffect, useRef } from "react";
import { type GameState, type GameSettings } from "@shared/schema";
import { allCategories } from "@/lib/categories";
import { FREE_TRIAL_DECK } from "@/lib/premiumDecks";
import { claimFreeTrial, hasClaimedFreeTrial } from "@/lib/premium";

export function useGameState(settings: GameSettings, playSound: (sound: string) => void) {
  const latestSettingsRef = useRef<GameSettings>(settings);

  // Keep ref in sync with latest settings
  useEffect(() => {
    latestSettingsRef.current = settings;
  }, [settings]);

  const totalRounds = settings.numberOfRounds === "infinite" ? undefined : parseInt(settings.numberOfRounds);

  const [gameState, setGameState] = useState<GameState>({
    status: "welcome",
    score: 0,
    currentWord: null,
    currentCategory: null,
    timeRemaining: parseInt(settings.timerLength),
    wordsGuessed: [],
    currentRound: 1,
    totalRounds,
    gameMode: settings.gameMode || "solo",
    teams: [],
    currentTeamIndex: 0,
    activeCategories: settings.selectedCategories,
  });

  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const usedWordsRef = useRef<Set<string>>(new Set());

  const getRandomWord = (overrideCategories?: string[]) => {
    const selectedCategories = overrideCategories || gameState.activeCategories;
    const availableCategories = allCategories.filter((c) =>
      selectedCategories.includes(c.id)
    );

    if (availableCategories.length === 0) return null;

    const randomCategory =
      availableCategories[Math.floor(Math.random() * availableCategories.length)];

    const availableWords = randomCategory.words.filter(
      (word) => !usedWordsRef.current.has(word)
    );

    if (availableWords.length === 0) {
      usedWordsRef.current.clear();
      return {
        word: randomCategory.words[Math.floor(Math.random() * randomCategory.words.length)],
        categoryId: randomCategory.id,
      };
    }

    const randomWord =
      availableWords[Math.floor(Math.random() * availableWords.length)];
    usedWordsRef.current.add(randomWord);

    return {
      word: randomWord,
      categoryId: randomCategory.id,
    };
  };

  // Helper function to determine if we should show round-end screen
  const shouldShowRoundEnd = (state: GameState): boolean => {
    if (state.gameMode === "solo") {
      const hasMoreRounds = !state.totalRounds || state.currentRound < state.totalRounds;
      return hasMoreRounds;
    }

    if (state.gameMode === "team" && state.teams && state.teams.length > 1) {
      const currentIndex = state.currentTeamIndex || 0;
      const isLastTeam = currentIndex === state.teams.length - 1;
      const hasMoreRounds = !state.totalRounds || state.currentRound < state.totalRounds;
      return !isLastTeam || hasMoreRounds;
    }

    return false;
  };

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const startTimer = () => {
    // Always clear any existing timer first
    stopTimer();

    timerRef.current = setInterval(() => {
      setGameState((prev) => {
        if (prev.timeRemaining <= 1) {
          playSound("timeout");
          stopTimer();

          // Use helper function to determine if round-end or game-end
          if (shouldShowRoundEnd(prev)) {
            return { ...prev, status: "round-end", timeRemaining: 0 };
          }

          return { ...prev, status: "ended", timeRemaining: 0 };
        }

        if (prev.timeRemaining === 10) {
          playSound("tick");
        }

        return { ...prev, timeRemaining: prev.timeRemaining - 1 };
      });
    }, 1000);
  };

  const startGame = (teams?: Array<{ name: string; score: number; color: string }>, overrideSettings?: GameSettings) => {
    // Use override settings directly when provided (already merged by caller), otherwise use latest ref
    const effectiveSettings = overrideSettings ?? latestSettingsRef.current;
    const gameMode = effectiveSettings.gameMode || "solo";
    const totalRounds = effectiveSettings.numberOfRounds === "infinite" 
      ? undefined 
      : parseInt(effectiveSettings.numberOfRounds || "5");

    setGameState({
      status: gameMode === "team" ? "team-setup" : "category-select",
      score: 0,
      currentWord: null,
      currentCategory: null,
      timeRemaining: parseInt(effectiveSettings.timerLength),
      wordsGuessed: [],
      currentRound: 1,
      totalRounds,
      gameMode,
      teams: teams || [],
      currentTeamIndex: 0,
      activeCategories: effectiveSettings.selectedCategories,
    });
    usedWordsRef.current.clear();
  };

  const startWithTeams = (teams: Array<{ name: string; score: number; color: string }>, overrideSettings?: GameSettings) => {
    // Use override settings directly when provided (already merged by caller), otherwise use latest ref
    const effectiveSettings = overrideSettings ?? latestSettingsRef.current;
    const totalRounds = effectiveSettings.numberOfRounds === "infinite" 
      ? undefined 
      : parseInt(effectiveSettings.numberOfRounds || "5");

    setGameState({
      status: "category-select",
      score: 0,
      currentWord: null,
      currentCategory: null,
      timeRemaining: parseInt(effectiveSettings.timerLength),
      wordsGuessed: [],
      currentRound: 1,
      totalRounds,
      gameMode: "team",
      teams,
      currentTeamIndex: 0,
      activeCategories: effectiveSettings.selectedCategories,
    });
    usedWordsRef.current.clear();
  };

  // FIX: Pass isInitialWord to control timer start, avoid stale gameState closure
  const nextWord = (overrideCategories?: string[], isInitialWord?: boolean) => {
    const wordData = getRandomWord(overrideCategories);
    if (!wordData) {
      setGameState((prev) => ({ ...prev, status: "welcome" }));
      return;
    }

    // Determine if we should start the timer based on the parameter
    const shouldStartTimer = isInitialWord === true;

    setGameState((prev) => ({
      ...prev,
      status: "playing",
      currentWord: wordData.word,
      currentCategory: wordData.categoryId,
      activeCategories: overrideCategories || prev.activeCategories,
    }));

    if (shouldStartTimer) {
      playSound("start");
      startTimer();
    }
  };

  const correctGuess = () => {
    const wordData = getRandomWord();
    if (!wordData) return;

    setGameState((prev) => {
      if (prev.gameMode === "team" && prev.teams && prev.teams.length > 0) {
        const updatedTeams = [...prev.teams];
        const currentIndex = prev.currentTeamIndex || 0;
        updatedTeams[currentIndex] = {
          ...updatedTeams[currentIndex],
          score: updatedTeams[currentIndex].score + 1,
        };

        return {
          ...prev,
          teams: updatedTeams,
          score: prev.score + 1,
          currentWord: wordData.word,
          currentCategory: wordData.categoryId,
          wordsGuessed: [...prev.wordsGuessed, prev.currentWord || ""],
        };
      }

      return {
        ...prev,
        score: prev.score + 1,
        currentWord: wordData.word,
        currentCategory: wordData.categoryId,
        wordsGuessed: [...prev.wordsGuessed, prev.currentWord || ""],
      };
    });
  };

  // FIX: Clear used words and use latestSettingsRef for timer length
  const continueNextRound = () => {
    // Clear used words for the new round
    usedWordsRef.current.clear();

    setGameState((prev) => {
      if (prev.gameMode === "team" && prev.teams && prev.teams.length > 1) {
        const currentIndex = prev.currentTeamIndex || 0;
        const nextIndex = (currentIndex + 1) % prev.teams.length;
        const isMovingToNextRound = nextIndex === 0;

        // Force React to re-render the team display properly
        setTimeout(() => {
          setGameState({
            ...prev,
            status: "playing",
            timeRemaining: parseInt(latestSettingsRef.current.timerLength),
            currentTeamIndex: nextIndex,
            currentRound: isMovingToNextRound ? prev.currentRound + 1 : prev.currentRound,
          });
        }, 50);

        return prev; // temporary state to avoid flash
      } else {
        // Solo mode - just increment round
        return {
          ...prev,
          status: "playing",
          timeRemaining: parseInt(latestSettingsRef.current.timerLength),
          currentRound: prev.currentRound + 1,
        };
      }
    });

    playSound("start");
    startTimer();
  };

  const nextTeam = () => {
    stopTimer();
    setGameState((prev) => ({ ...prev, status: "round-end", timeRemaining: 0 }));
  };

  const pauseGame = () => {
    stopTimer();
    setGameState((prev) => ({ ...prev, status: "paused" }));
  };

  const resumeGame = () => {
    setGameState((prev) => ({ ...prev, status: "playing" }));
  };

  const endGame = () => {
    stopTimer();
    setGameState((prev) => {
      // Auto-unlock free trial deck after first game with 10+ words
      if (!hasClaimedFreeTrial() && prev.wordsGuessed.length >= 10) {
        claimFreeTrial(FREE_TRIAL_DECK.id);
      }

      return { ...prev, status: "ended" };
    });
  };

  const resetGame = () => {
    stopTimer();
    usedWordsRef.current.clear();
    const totalRounds = settings.numberOfRounds === "infinite" ? undefined : parseInt(settings.numberOfRounds);

    setGameState({
      status: "welcome",
      score: 0,
      currentWord: null,
      currentCategory: null,
      timeRemaining: parseInt(settings.timerLength),
      wordsGuessed: [],
      currentRound: 1,
      totalRounds,
      gameMode: settings.gameMode || "solo",
      teams: [],
      currentTeamIndex: 0,
      activeCategories: settings.selectedCategories,
    });
  };

  const addTime = (seconds: number) => {
    setGameState((prev) => ({
      ...prev,
      timeRemaining: Math.min(prev.timeRemaining + seconds, parseInt(latestSettingsRef.current.timerLength)),
    }));
  };

  // FIX: Effect to handle timer state changes (pause/resume)
  useEffect(() => {
    if (gameState.status === "playing") {
      startTimer();
    } else {
      stopTimer();
    }

    return () => {
      stopTimer();
    };
  }, [gameState.status]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopTimer();
    };
  }, []);

  return {
    gameState,
    startGame,
    startWithTeams,
    nextWord,
    correctGuess,
    pauseGame,
    resumeGame,
    endGame,
    resetGame,
    nextTeam,
    continueNextRound,
    addTime,
  };
}

