import { useState } from "react";
import { X, Crown, Sparkles, Zap, Lock, Check } from "lucide-react";
import { Button } from "@/components/ui/button";
import confetti from "canvas-confetti";

interface PaywallModalProps {
  isOpen: boolean;
  onClose: () => void;
  onUnlock: () => void;
}

type PricingTier = "monthly" | "yearly" | "lifetime";

export function PaywallModal({ isOpen, onClose, onUnlock }: PaywallModalProps) {
  const [selectedTier, setSelectedTier] = useState<PricingTier>("yearly");
  const [isUnlocking, setIsUnlocking] = useState(false);
  const [showThankYou, setShowThankYou] = useState(false);

  if (!isOpen) return null;

  const handleUnlock = async () => {
    setIsUnlocking(true);

    // Simulate payment processing
    await new Promise((resolve) => setTimeout(resolve, 1500));

    // TODO: RevenueCat integration here
    // Example:
    // const result = await Purchases.purchasePackage(selectedPackage);
    // if (result.customerInfo.entitlements.active['premium']) {
    //   onUnlock();
    // }

    // Show confetti celebration
    confetti({
      particleCount: 100,
      spread: 70,
      origin: { y: 0.6 },
      colors: ["#a855f7", "#06b6d4", "#f97316"],
    });

    // Trigger haptic feedback
    if (navigator.vibrate) {
      navigator.vibrate([200, 100, 200]);
    }

    setShowThankYou(true);

    // Call unlock and close after celebration
    setTimeout(() => {
      onUnlock();
      setIsUnlocking(false);
      setShowThankYou(false);
      onClose();
    }, 2000);
  };

  const pricingTiers = [
    {
      id: "monthly" as PricingTier,
      name: "Monthly",
      price: "$2.99",
      period: "/month",
      badge: null,
      savings: null,
    },
    {
      id: "yearly" as PricingTier,
      name: "Yearly",
      price: "$19.99",
      period: "/year",
      badge: "Best Value",
      savings: "Save 44%",
    },
    {
      id: "lifetime" as PricingTier,
      name: "Lifetime",
      price: "$9.99",
      period: "once",
      badge: "Most Popular",
      savings: null,
    },
  ];

  const premiumFeatures = [
    "35 Premium Decks (900+ words)",
    "Disney, Marvel, Harry Potter & More",
    "TikTok Dances & Pop Culture",
    "Adult 18+ Content",
    "Remove All Ads Forever",
    "Early Access to New Decks",
    "Support Development",
  ];

  if (showThankYou) {
    return (
      <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-6">
        <div className="bg-gradient-to-br from-primary/20 to-secondary/20 rounded-3xl p-8 max-w-md w-full text-center space-y-6 animate-in zoom-in duration-500">
          <div className="flex justify-center">
            <div className="bg-primary/20 p-6 rounded-full">
              <Crown className="w-16 h-16 text-primary" />
            </div>
          </div>
          <div className="space-y-2">
            <h2 className="text-3xl font-bold text-foreground">Welcome to Premium!</h2>
            <p className="text-muted-foreground text-lg">
              You now have access to all 44 categories!
            </p>
          </div>
          <div className="flex items-center justify-center gap-2 text-primary font-semibold">
            <Sparkles className="w-5 h-5" />
            <span>Unlocking premium features...</span>
            <Sparkles className="w-5 h-5" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-6 overflow-y-auto">
      <div className="bg-background rounded-3xl max-w-2xl w-full my-8 animate-in zoom-in duration-300">
        {/* Header */}
        <div className="relative bg-gradient-to-br from-primary to-secondary p-8 rounded-t-3xl">
          <button
            onClick={onClose}
            className="absolute top-4 right-4 text-primary-foreground/80 hover:text-primary-foreground transition-colors"
            data-testid="button-paywall-close"
            aria-label="Close paywall modal"
          >
            <X className="w-6 h-6" />
          </button>

          <div className="text-center space-y-3">
            <div className="flex justify-center">
              <div className="bg-primary-foreground/20 p-4 rounded-full">
                <Crown className="w-12 h-12 text-primary-foreground" />
              </div>
            </div>
            <h2 className="text-3xl md:text-4xl font-bold text-primary-foreground">
              Unlock Premium
            </h2>
            <p className="text-primary-foreground/90 text-lg">
              Get access to 35+ exclusive decks
            </p>
          </div>
        </div>

        {/* Pricing Tiers */}
        <div className="p-6 space-y-4">
          <div className="grid gap-3">
            {pricingTiers.map((tier) => (
              <button
                key={tier.id}
                onClick={() => setSelectedTier(tier.id)}
                className={`relative p-4 rounded-2xl border-2 transition-all hover-elevate ${
                  selectedTier === tier.id
                    ? "border-primary bg-primary/10"
                    : "border-border bg-card"
                }`}
                data-testid={`button-tier-${tier.id}`}
              >
                {tier.badge && (
                  <div className="absolute -top-3 left-1/2 -translate-x-1/2">
                    <span className="bg-primary text-primary-foreground px-3 py-1 rounded-full text-xs font-bold shadow-lg flex items-center gap-1">
                      <Zap className="w-3 h-3" />
                      {tier.badge}
                    </span>
                  </div>
                )}

                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-4">
                    <div
                      className={`w-6 h-6 rounded-full border-2 flex items-center justify-center ${
                        selectedTier === tier.id
                          ? "border-primary bg-primary"
                          : "border-muted-foreground/30"
                      }`}
                    >
                      {selectedTier === tier.id && (
                        <Check className="w-4 h-4 text-primary-foreground" />
                      )}
                    </div>
                    <div className="text-left">
                      <div className="font-bold text-foreground">{tier.name}</div>
                      {tier.savings && (
                        <div className="text-sm text-primary font-semibold">
                          {tier.savings}
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-2xl font-bold text-foreground">{tier.price}</div>
                    <div className="text-sm text-muted-foreground">{tier.period}</div>
                  </div>
                </div>
              </button>
            ))}
          </div>

          {/* Features */}
          <div className="bg-card rounded-2xl p-6 space-y-3">
            <h3 className="font-bold text-foreground flex items-center gap-2">
              <Sparkles className="w-5 h-5 text-primary" />
              What You Get:
            </h3>
            <div className="space-y-2">
              {premiumFeatures.map((feature, index) => (
                <div key={index} className="flex items-center gap-3 text-sm">
                  <Check className="w-4 h-4 text-primary flex-shrink-0" />
                  <span className="text-muted-foreground">{feature}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Unlock Button */}
          <Button
            size="lg"
            className="w-full h-14 text-lg font-bold rounded-2xl"
            onClick={handleUnlock}
            disabled={isUnlocking}
            data-testid="button-unlock-premium"
          >
            {isUnlocking ? (
              <div className="flex items-center gap-2">
                <div className="w-5 h-5 border-2 border-primary-foreground/30 border-t-primary-foreground rounded-full animate-spin" />
                Processing...
              </div>
            ) : (
              <div className="flex items-center gap-2">
                <Lock className="w-5 h-5" />
                Unlock Now - {pricingTiers.find((t) => t.id === selectedTier)?.price}
              </div>
            )}
          </Button>

          {/* Footer */}
          <div className="text-center space-y-2">
            <p className="text-xs text-muted-foreground">
              Simulated purchase - No real payment required
            </p>
            <button
              onClick={onClose}
              className="text-sm text-muted-foreground hover:text-foreground transition-colors"
              data-testid="button-maybe-later"
            >
              Maybe Later
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/game/DeckShop.tsx.content:
import { useState } from "react";
import { ArrowLeft, Lock, Sparkles, TrendingUp } from "lucide-react";
import { Button } from "@/components/ui/button";
import { CategoryCard } from "./CategoryCard";
import { allCategories } from "@/lib/categories";
import { Badge } from "@/components/ui/badge";
import { isDeckUnlocked } from "@/lib/premium";

interface DeckShopProps {
  onBack: () => void;
  onOpenPaywall: () => void;
  isPremium: boolean;
  selectedCategories?: string[];
  onCategoryToggle?: (categoryId: string) => void;
}

export function DeckShop({
  onBack,
  onOpenPaywall,
  isPremium,
  selectedCategories = [],
  onCategoryToggle,
}: DeckShopProps) {
  const [filter, setFilter] = useState<"all" | "free" | "premium">("all");

  // Tag certain decks as "New" or "Popular"
  const newDeckIds = ["tiktok-dances", "billie-eilish", "reality-tv", "memes", "emojis"];
  const popularDeckIds = ["disney", "marvel", "harry-potter", "friends-quotes", "the-office", "netflix-shows"];

  const filteredCategories = allCategories.filter((cat) => {
    if (filter === "free") return !cat.premium;
    if (filter === "premium") return cat.premium;
    return true;
  });

  const freeCount = allCategories.filter((cat) => !cat.premium).length;
  const premiumCount = allCategories.filter((cat) => cat.premium).length;

  return (
    <div className="min-h-screen flex flex-col p-6 md:p-8">
      {/* Fixed Back Button */}
      <div className="fixed top-4 left-4 z-50">
        <Button
          variant="ghost"
          size="icon"
          onClick={onBack}
          className="bg-background/80 backdrop-blur-sm rounded-full shadow-lg"
          data-testid="button-back-fixed"
        >
          <ArrowLeft className="h-6 w-6" />
        </Button>
      </div>

      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <Button
          size="icon"
          variant="ghost"
          onClick={onBack}
          data-testid="button-back-from-shop"
        >
          <ArrowLeft className="h-6 w-6" />
        </Button>
        <h2 className="text-2xl md:text-3xl font-bold text-foreground">
          Deck Shop
        </h2>
        <div className="w-10" />
      </div>

      {/* Stats */}
      <div className="flex items-center justify-center gap-4 mb-6">
        <div className="text-center">
          <div className="text-3xl font-bold text-primary">{allCategories.length}</div>
          <div className="text-sm text-muted-foreground">Total Decks</div>
        </div>
        <div className="w-px h-12 bg-border" />
        <div className="text-center">
          <div className="text-3xl font-bold text-green-500">{freeCount}</div>
          <div className="text-sm text-muted-foreground">Free</div>
        </div>
        <div className="w-px h-12 bg-border" />
        <div className="text-center">
          <div className="text-3xl font-bold text-purple-500">{premiumCount}</div>
          <div className="text-sm text-muted-foreground">Premium</div>
        </div>
      </div>

      {/* Premium Banner (if not premium) */}
      {!isPremium && (
        <div
          className="bg-gradient-to-br from-primary/20 to-secondary/20 rounded-2xl p-6 mb-6 border-2 border-primary/30 hover-elevate cursor-pointer"
          onClick={onOpenPaywall}
          data-testid="banner-upgrade-premium"
        >
          <div className="flex items-center justify-between gap-4">
            <div className="flex-1">
              <div className="flex items-center gap-2 mb-2">
                <Sparkles className="w-5 h-5 text-primary" />
                <h3 className="font-bold text-lg text-foreground">Unlock All Premium Decks</h3>
              </div>
              <p className="text-sm text-muted-foreground mb-3">
                Get instant access to {premiumCount} exclusive decks with 900+ premium words
              </p>
              <Button size="sm" variant="default" data-testid="button-upgrade-from-banner">
                <Lock className="w-4 h-4 mr-2" />
                Unlock Premium
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Filter Tabs */}
      <div className="flex gap-2 mb-6">
        <Button
          variant={filter === "all" ? "default" : "outline"}
          size="sm"
          onClick={() => setFilter("all")}
          data-testid="filter-all"
        >
          All ({allCategories.length})
        </Button>
        <Button
          variant={filter === "free" ? "default" : "outline"}
          size="sm"
          onClick={() => setFilter("free")}
          data-testid="filter-free"
        >
          Free ({freeCount})
        </Button>
        <Button
          variant={filter === "premium" ? "default" : "outline"}
          size="sm"
          onClick={() => setFilter("premium")}
          data-testid="filter-premium"
        >
          Premium ({premiumCount})
        </Button>
      </div>

      {/* Deck Grid */}
      <div className="flex-1 max-w-6xl w-full mx-auto">
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 pb-8">
          {filteredCategories.map((category) => {
            const isLocked = !isDeckUnlocked(category.id) && category.premium;
            const isNew = newDeckIds.includes(category.id);
            const isPopular = popularDeckIds.includes(category.id);
            const isSelected = selectedCategories.includes(category.id);

            return (
              <div key={category.id} className="relative">
                {/* Tags */}
                {(isNew || isPopular) && (
                  <div className="absolute -top-2 -right-2 z-10">
                    {isNew && (
                      <Badge
                        variant="default"
                        className="bg-green-500 hover:bg-green-600 text-white font-bold shadow-lg"
                      >
                        <Sparkles className="w-3 h-3 mr-1" />
                        New
                      </Badge>
                    )}
                    {isPopular && !isNew && (
                      <Badge
                        variant="default"
                        className="bg-orange-500 hover:bg-orange-600 text-white font-bold shadow-lg"
                      >
                        <TrendingUp className="w-3 h-3 mr-1" />
                        Popular
                      </Badge>
                    )}
                  </div>
                )}

                {/* Category Card */}
                <CategoryCard
                  category={category}
                  isSelected={isSelected}
                  onToggle={() => {
                    if (isLocked) {
                      onOpenPaywall();
                    } else if (onCategoryToggle) {
                      onCategoryToggle(category.id);
                    }
                  }}
                  isLocked={isLocked}
                  onLockedClick={onOpenPaywall}
                />
              </div>
            );
          })}
        </div>
      </div>

      {/* Bottom Info */}
      <div className="text-center text-sm text-muted-foreground py-4">
        {isPremium ? (
          <p className="flex items-center justify-center gap-2">
            <Sparkles className="w-4 h-4 text-primary" />
            You have access to all {allCategories.length} decks!
          </p>
        ) : (
          <p>
            Unlock {premiumCount} premium decks with{" "}
            <button
              onClick={onOpenPaywall}
              className="text-primary font-semibold hover:underline"
              data-testid="button-upgrade-inline"
            >
              Premium
            </button>
          </p>
        )}
      </div>
    </div>
  );
}

